function reducer(state, action) {


    switch (action.type) {
        case DECREASE:
            return { ...state, count: state.count - 1 }

        case INCREASE:
            return { ...state, count: state.count + 1 }

        case CLEAR_CART:
            return { ...state, cart: [] }

        case REMOVE:
            console.log("");   

        default:
            return state;

